/**
 * Represents an individual player, along with all his games.
 * @param playerData The data from which to build the player
 * @constructor
 */
function Player(playerData){

	//Copy properties from playerData directly to the newly created Player object
	_.each(playerData, _.bind(function(dataValue, dataField){
		this[dataField] = dataValue;
	}, this));

	this.href = "/"+this.id;      //this is the URL that will represent the player in our view

	//Now's a good time to generate some game stats and convert the date to a JavaScript date objects. We could do this on-the-fly later, but it wouldn't be very performative.
	_.each(this.games, function(game){
		game.AVG = Math.round(game.H / game.AB * 1000)/1000;            // The average
		game.friendlyDate = game.date;          // Keep a copy of the original date string.
		game.date = parseGameDate(game.date);   //Turn string dates into JavaScript dates
	});


	/**
	 * Turns game dates into JavaScript date objects
	 * @param gameDate
	 */
	function parseGameDate(gameDate){
		var dateParts = gameDate.match(/^(\d+)-(\d+)-(\d+)$/);
		if(!dateParts) {
			throw new Error("Game date is malformed.");
		} else {
			return new Date(dateParts[1], dateParts[2] - 1, dateParts[3]);
		}
	}

	//For charting purposes, I'd like to know what the first and last game dates are. Might as well sort the game list by date.
	this.games = _.sortBy(this.games, "date");
	this.firstGame = this.games[0];
	this.lastGame = this.games[this.games.length - 1];

	//Generate some aggregate stats -- things like overall average, season home runs, and the like
	this.aggregateStats = new PlayerAggregateStats(this.games);
}

/**
 * Generates some stats based on all the games a player has been in
 * @param {[object]} games An array of game objects
 * @constructor
 */
function PlayerAggregateStats(games){

	/**
	 * Sums up a particular stat from all the games
	 * @param {[Object]} games The array of games
	 * @param {String} stat The stat to total up
	 * @returns {Number}
	 */
	function sumUp(games, stat){
		return _.reduce(games, function(total, game){
			return total += game[stat];
		}, 0);
	}

	this.total = {};

	this.total.games = games.length;
	this.total.H = sumUp(games, "H");
	this.total.HR = sumUp(games, "HR");
	this.total.RBI = sumUp(games, "RBI");
	this.total.AB = sumUp(games, "AB");

	//It makes no sense to total up all the averages, but we can at least calculate the overall average
	this.total.AVG = Math.round(this.total.H / this.total.AB * 1000)/1000;
}

